var inp = global.get("input")
msg = {
    location:{
        city: inp.city,
        country: inp.country

    }

}
return msg;

// Generiert eine zufällige Temperatur zwischen 20 und 24 Grad Celsius
var temperatur = 20 + Math.random() * 4; // Multiplikator auf 4 geändert, um den Bereich von 20 bis 24 abzudecken

// Rundet die Temperatur auf eine Kommastelle
msg.payload = Math.round(temperatur * 10) / 10;

return msg;

// Zugriff auf die Daten
let data = msg.payload;

// Zählen der Objekte, bei denen 'entry' auf true gesetzt ist
let countTrueEntries = data.reduce((acc, current) => {
  // Stelle sicher, dass der Wert in 'entry' als Boolean interpretiert wird (falls notwendig)
  if (current.entry === true || current.entry === "true") {
    acc += 1;
  }
  return acc;
}, 0);

// Speichere die Anzahl in der msg.payload für die Ausgabe
msg.payload = countTrueEntries;

return msg;

let data = msg.payload;
// Finde das größte Datum in den Datensätzen
let latestDate = new Date(Math.max(...data.map(e => new Date(e.date))));

// Finde das vollständige Objekt, das das neueste Datum enthält
let latestRecord = data.find(e => new Date(e.date).getTime() === latestDate.getTime());

// Setze das vollständige Objekt als Payload, damit du auf alle seine Attribute zugreifen kannst
msg.payload = latestRecord;

return msg;

if(msg.payload > 23.9)
{
    msg.topic = "Subject: System temperature has been exceeded.";
    msg.payload = "Temperature: " + String(msg.payload);
    return msg;
}

// Überprüfen Sie, ob eine globale Variable 'empl' existiert und größer als 0 ist
if (global.get("empl") > 0) {
    // Entscheidung treffen, basierend auf der Temperatur in msg.payload
    if (msg.payload < 22) {
        msg.payload = true;  // Heizung einschalten
    } else {
        msg.payload = false; // Heizung ausschalten
    }
}
return msg;

if (msg.payload == true) {
    msg.payload = Math.floor(Math.random() * 40) + 1;
} else {
    msg.payload = 0;
}
return msg;

// Angenommen, time ist ein Timestamp oder ein Datum-String, der vom Date Konstruktor akzeptiert wird
time = msg.payload;

// Erstelle ein neues Date-Objekt
var date = new Date(time);

// Array von Wochentagen auf Deutsch
var days = ["Sonntag", "Montag", "Dienstag", "Mittwoch", "Donnerstag", "Freitag", "Samstag"];

// Formatierung der Ausgabe
var formattedDate = days[date.getDay()] + ", " +
    ("0" + date.getDate()).slice(-2) + "." +
    ("0" + (date.getMonth() + 1)).slice(-2) + "." +
    date.getFullYear() + ", " +
    ("0" + date.getHours()).slice(-2) + ":" +
    ("0" + date.getMinutes()).slice(-2);

// Setze das formatierte Datum zurück in msg.payload
msg.payload = formattedDate;

return msg;

// Initialisieren der Zählvariablen
let zoneA = 0;
let zoneB = 0;
let zoneC = 0;

// Durchlaufen jedes Datensatzes
msg.payload.forEach(function (record) {
    // Prüfe, ob der Eintrag in 'entry' true ist
    if (record.entry === true || record.entry === "true") {  // Achte darauf, den korrekten Datentyp zu prüfen
        switch (record.zone) { 
            case 'A':
                zoneA++;
                break;
            case 'B':
                zoneB++;
                break;
            case 'C':
                zoneC++;
                break;
        }
    }
});

// Erstellen der Ausgabemessage mit den Zählungen
msg.payload = {
    zoneA: zoneA,
    zoneB: zoneB,
    zoneC: zoneC
};

return msg;

// Umformatieren der Daten für das Balkendiagramm
msg.payload = [
    {
        "series": ["Mitarbeiterzahlen"],
        "data": [[msg.payload.zoneA, msg.payload.zoneB, msg.payload.zoneC]],
        "labels": ["Zone A", "Zone B", "Zone C"]
    }
];
return msg;

// Zugriff auf die Datenstruktur
