let entries = [];

try {
    entries = JSON.parse(msg.payload);
} catch (err) {
    node.error('Error parsing log file: ' + err);
}

msg.payload = entries.map(e => {
    return `<tr><td>${e.time}</td><td>${e.name}</td><td>${e.room}</td><td>${e.state}</td></tr>`;
}).join('');

return msg;

let climateData = [];

try {
    climateData = JSON.parse(msg.payload);
} catch (err) {
    node.error('Error parsing log file: ' + err);
}

if (typeof climateData === 'object' && !Array.isArray(climateData)) {
    climateData = Object.entries(climateData).map(([room, data]) => ({ room, ...data }));
}

msg.payload = climateData.map(e => {
    return `<tr>
                <td>${e.room}</td>
                <td>${e.count}</td>
                <td>${e.state}</td>
            </tr>`;
}).join('');

return msg;

let entries = [];

try {
    entries = JSON.parse(msg.payload);
} catch (err) {
    node.error('Error parsing log file: ' + err);
}

let options = entries
    .filter(entry => entry.image) // filter out entries with empty image paths
    .map(entry => ({ "label": entry.image.split('/').pop(), "value": entry.image }));
msg.options = options;
msg.payload = options;
return msg;
